import React from 'react';
import Canvas from './components/Canvas';
import useGameLogic from './hooks/useGameLogic';
import SettingsPanel from './components/SettingsPanel';
import Menu from './components/Menu';

const App = () => {
  const { heroes, bullets, fireBullet, setHeroes } = useGameLogic();

  const draw = (context) => {
    context.clearRect(0, 0, 800, 400);

    // Draw heroes
    Object.keys(heroes).forEach((heroKey) => {
      const hero = heroes[heroKey];
      context.beginPath();
      context.arc(hero.x, hero.y, hero.radius, 0, Math.PI * 2);
      context.fillStyle = hero.color;
      context.fill();
      context.closePath();
    });

    // Draw bullets
    Object.keys(bullets).forEach((bulletKey) => {
      bullets[bulletKey].forEach((bullet) => {
        context.beginPath();
        context.arc(bullet.x, bullet.y, 5, 0, Math.PI * 2);
        context.fillStyle = 'black';
        context.fill();
        context.closePath();
      });
    });
  };

  const handleMouseMove = (e) => {
    const rect = e.target.getBoundingClientRect();
    const mouseX = e.clientX - rect.left;
    const mouseY = e.clientY - rect.top;

    Object.keys(heroes).forEach((heroKey) => {
      const hero = heroes[heroKey];
      const distance = Math.sqrt((hero.x - mouseX) ** 2 + (hero.y - mouseY) ** 2);

      if (distance < hero.radius + 10) { // 10 - небольшой отступ для чувствительности
        hero.dy = -hero.dy; // меняем направление движения
        setHeroes({ ...heroes, [heroKey]: hero });
      }
    });
  };

  return (
    <div>
      <Canvas draw={draw} onClick={() => {}} onMouseMove={handleMouseMove} />
      <SettingsPanel heroConfig={heroes.hero1} onConfigChange={() => {}} />
      <SettingsPanel heroConfig={heroes.hero2} onConfigChange={() => {}} />
      <Menu hero={heroes.hero1} onChangeColor={() => {}} />
      <Menu hero={heroes.hero2} onChangeColor={() => {}} />
    </div>
  );
};

export default App;
