import { useState, useEffect } from 'react';
import { detectCollision } from '../utils/collisionDetection'; // Исправлен импорт

const useGameLogic = () => {
  const [heroes, setHeroes] = useState({
    hero1: { x: 30, y: 200, radius: 20, dy: 2, color: 'red' },
    hero2: { x: 770, y: 200, radius: 20, dy: 2, color: 'blue' },
  });

  const [bullets, setBullets] = useState({ bullets1: [], bullets2: [] });

  const updateHeroes = () => {
    setHeroes((prevHeroes) => {
      const newHeroes = { ...prevHeroes };
      Object.keys(newHeroes).forEach((heroKey) => {
        const hero = newHeroes[heroKey];

        // Обновляем положение героев
        hero.y += hero.dy;

        // Если герой достиг верхней или нижней границы, меняем направление
        if (hero.y <= hero.radius || hero.y >= 400 - hero.radius) {
          hero.dy = -hero.dy;
        }
      });
      return newHeroes;
    });
  };

  const fireBullet = (heroKey, direction) => {
    setBullets((prevBullets) => ({
      ...prevBullets,
      [heroKey]: [
        ...prevBullets[heroKey], 
        { x: heroes[heroKey].x + (direction === 'right' ? heroes[heroKey].radius : -heroes[heroKey].radius), 
          y: heroes[heroKey].y, 
          dx: direction === 'right' ? 5 : -5 
        }
      ]
    }));
  };

  const updateBullets = () => {
    setBullets((prevBullets) => {
      const newBullets = { ...prevBullets };
      Object.keys(newBullets).forEach((key) => {
        newBullets[key] = newBullets[key].map((bullet) => {
          bullet.x += bullet.dx;
          return bullet;
        }).filter((bullet) => {
          const targetHero = key === 'bullets1' ? heroes.hero2 : heroes.hero1;
          if (detectCollision(bullet, targetHero)) {
            // Если происходит столкновение, удаляем заклинание
            return false;
          }
          return bullet.x > 0 && bullet.x < 800;
        });
      });
      return newBullets;
    });
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      updateHeroes();
      updateBullets();
    }, 1000 / 60); // 60 кадров в секунду

    return () => clearInterval(intervalId);
  }, [heroes, bullets]);

  useEffect(() => {
    const fireInterval1 = setInterval(() => {
      fireBullet('bullets1', 'right');
    }, 1000); // Стрельба с интервалом 1 секунда

    const fireInterval2 = setInterval(() => {
      fireBullet('bullets2', 'left');
    }, 1000);

    return () => {
      clearInterval(fireInterval1);
      clearInterval(fireInterval2);
    };
  }, [fireBullet]);

  return { heroes, bullets, fireBullet, setHeroes };
};

export default useGameLogic;
