import React from 'react';

const Menu = ({ hero, onChangeColor }) => {
  return (
    <div style={{ position: 'absolute', top: hero.y, left: hero.x }}>
      <input
        type="color"
        value={hero.color}
        onChange={(e) => onChangeColor(hero.id, e.target.value)}
      />
    </div>
  );
};

export default Menu;
