import React, { useRef, useEffect } from 'react';

const fireBullet = (heroKey, direction) => {
  const newBullet = {
    x: heroes[heroKey].x + (direction === 'right' ? heroes[heroKey].radius : -heroes[heroKey].radius),
    y: heroes[heroKey].y,
    dx: direction === 'right' ? 5 : -5,
    color: 'red'  // Use a bright color to check visibility
  };

  console.log(`Firing bullet from ${heroKey}:`, newBullet);

  setBullets((prevBullets) => ({
    ...prevBullets,
    [heroKey]: [...prevBullets[heroKey], newBullet]
  }));
};


const Canvas = ({ draw, onClick, onMouseMove }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    let animationFrameId;

    const render = () => {
      draw(context);
      animationFrameId = requestAnimationFrame(render);
    };

    render();

    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, [draw]);

  return (
    <canvas
      ref={canvasRef}
      width={800}
      height={400}
      style={{ border: '1px solid black' }}
      onClick={onClick}
      onMouseMove={onMouseMove}
    />
  );
};

export default Canvas;
